sition: center center;\n	background-repeat: no-repeat;\n}\n/* Customize - Container */\n#customize_container\n{\n	padding: 0% 5%;\n}\n\n/* Menu top */\n.navbar-default\n{\n	background: #fff;\n	border: 0px;\nbox-shadow: 0px 1px 5px 3px rgba(0,0,0,0.2); \n}\n#menu-menu-dau-trang li a\n{\n	background: #fff;\n	color: #000; \n	font-size: 16px\n}\n#menu-menu-dau-trang li a:hover\n{\n	background: #0B9F49;\n	color: #fff;\n}\n#menu-menu-dau-trang .active a\n{\n	background: #000;\n	color: #fff\n}\n\n/* Search on menu */\n.woocommerce-product-search button\n{\n	background-image: url(\'http://localhost/nxb/wp-content/uploads/2020/images/button_search.png\');\n	color: #000;\n	background-position: center center;\n	background-size: cover;\n}\n\n/**********************************\n *  POSTS\n *********************************/\n.content-area{\n	background: #fff;\n	border-radius: 10px;\n	padding: 0px 20px 10px 20px;\n	box-shadow: 1px 1px 3px 3px rgba(0,0,0,0.1);\n}\n.more-link,.edit-link,.comments-link\n{\n	padding: 10px;\n	color: #fff;\n	border-radius: 5px;\n}\n.more-link{background: #337ab7}\n.more-link:hover{\n	background: #000;\n	color: #fff;\n	text-decoration: none;\n}\n.edit-link{background: #F87613}\n.edit-link a{color: #fff}\n.comments-link{background: #347215}\n.comments-link a{color: #fff}', 'basicstore', '', 'inherit', 'closed', 'closed', '', '110-revision-v1', '', '', '2020-06-12 07:28:03', '2020-06-12 07:28:03', '', 110, 'http://localhost/nxb/2020/06/12/110-revision-v1/', 0, 'revision', '', 0),
