(6, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2020-06-03 07:01:21', NULL, 0),
(7, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Tips, product updates, and inspiration', 'Chúng tôi ở đây cùng bạn — nhận các mẹo, thông tin, và cảm hứng trực tiếp đến email của bạn.', 'mail', '{}', 'unactioned', 'woocommerce-admin', '2020-06-03 07:01:21', NULL, 0),
(8, 'wc-admin-store-notice-giving-feedback', 'info', 'en_US', 'Review your experience', 'If you like WooCommerce Admin please leave us a 5 star rating. A huge thanks in advance!', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-06-03 15:18:46', NULL, 0),
